mapper.select:
SELECT C.CNO cno,T.BSDATE bsdate,T.MTCN mtcn,T.TRD_USD trdUsd,T.TRD_KRW trdKrw,T.CNTCD cntCd,R.PASS_FNM passFnm,R.PASS_LNM passLnm,T.EXRATE exrate
FROM ( SELECT C.CNO 
		FROM CUSTOMER C 
		WHERE CNO = #{cno}
		) C,TRDHR T, RCPT R
WHERE T.CNO = C.CNO 
AND C.CNO = R.CNO
AND T.MTCN = R.MTCN
LIMIT ${startRow}, ${pageSize}

select count(*)
from ( SELECT C.CNO 
		FROM CUSTOMER C 
		WHERE CNO = #{cno}
		) C,TRDHR T
WHERE T.CNO = C.CNO
