mapper.select:
SELECT *
FROM CUSTOMER
WHERE CEMAIL LIKE #{cemail} AND CPWD LIKE #{cpwd}

SELECT COUNT(*) 
FROM CUSTOMER
WHERE CEMAIL like #{cemail}

mapper.insert:
INSERT INTO CUSTOMER ( CEMAIL, CPWD, CNAME, CNTCD, CPHONE, 
						CSTCD, SDATE, WDATE, UDATE ) 
VALUES (
	#{cemail}, #{cpwd}, #{cname}, #{cntcd}, #{cphone}, 
	#{cstcd}, #{sdate}, #{wdate}, #{udate}
)

mapper.delete:
DELETE
FROM CUSTOMER
WHERE CEMAIL LIKE #{cemail}

mapper.select:
SELECT CEMAIL, CNAME, CNTCD, ZIP, ADDR, CPHONE
FROM CUSTOMER
WEHRE CEMAIL LIKE #{cemail}

mapper.update:
UPDATE CUSTOMER
SET CPWD = #{cpwd}
WHERE CEMAIL = #{cemail}
AND CNO = #{cno}
